INSERT INTO `tbl_content` VALUES ('579', 'TR', '2', '57218', 'Web Tasarım', '', '', '', '', '<p class=\"text-justify\">Kurumsal firmalar, kişiler, kurumlar veya çeşitli organizasyonlar hedef kitlesine ulaşabilmek için web dünyasında yerini almak için yarışıyor. Web siteniz doğru bir altyapı ve tasarım ile hazırlandığında hedef kitlenize ulaşmanın en kolay ve ucuz yoludur.</p>\r\n<p class=\"text-justify\"><img class=\"img-left no-effect\" title=\"Web Tasarım\" src=\"upload/sayfa/s_57218/web-tasarim-web-design.png\" alt=\"Web Tasarım\" width=\"150\" height=\"150\" /></p>\r\n<p class=\"text-justify\"><a title=\"Web Tasarım\" href=\"#ewy_home\"><strong>webtasarımx.net</strong></a> olarak ihtiyaçlarınız doğrultusunda sizi, işinizi, rakiplerinizi ve hedef kitlenizi en iyi şekilde analiz ederek size en uygun, benzersiz, amaca uygun, güncel ve <a title=\"Dinamik Web Sitesi Nedir?\" href=\"faydali-bilgiler/dinamik-web-sitesi-nedir/\"><strong>dinamik web tasarımı</strong></a> oluşturuyoruz.</p>\r\n<p class=\"text-justify\">Oluşturduğumuz <em>dinamik web tasarımı</em> ile herhangi bir desteğe ihtiyaç duymadan günün şartlarına uygun olarak sitenizin yönetim panelinden güncelleyerek günün ihtiyaçlarına göre web sitenizi güncelleyebilirsiniz.</p>', '0', '', '');
